import React, { useEffect } from "react";
import { Container, ListGroup, ListGroupItem } from "react-bootstrap";
import { useState } from "react";

const Quiz = () => {

    const alertClicked = () => {
        alert('You clicked one of the buttons!');
    };

    const options = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px", 
    };

    const question = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px",
        backgroundColor: "#2F6BC1",
        color: "#FFFFFF",
    };

    const main = {
        width: "40%",
        margin: "auto",
    }

    const [questions, setQuestions] = useState([]);
    const [activeQuestion, setActiveQuestion] = useState(0)
    const [selectedAnswer, setSelectedAnswer] = useState('')
    /*const [result, setResult] = useState({
        score: 0,
        correctAnswers: 0,
        wrongAnswers: 0,
    })*/
    
    

    useEffect(() => {
        fetch('http://localhost:3001/api/questions')
        .then(response => {
            setQuestions(response.data);
            setActiveQuestion(response.data[0]);
        })
        .catch(error => console.error('Error fetching questions: ', error));
    }, []);

    return (
        <Container style={main}>
            <div>
                <h3 className="text-center p-3 mb-4 mt-5" style={question}>
                    {activeQuestion.text}
                </h3>
                <ListGroup vertical>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        1st Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        2nd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        3rd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        4th Answer
                    </ListGroupItem>
                </ListGroup>
            </div>
        </Container>
    );
}

export default Quiz;
