import React, { useEffect } from "react";
import { Container, ListGroup, ListGroupItem } from "react-bootstrap";
import { useState } from "react";

const Quiz = () => {
    
    const options = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px", 
    };
    
    const question_style = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px",
        backgroundColor: "#2F6BC1",
        color: "#FFFFFF",
    };
    
    const main = {
        width: "40%",
        margin: "auto",
    }
    
    const onClickNext = (question, answer) => {
        setActiveQuestion((prev) => prev + 1);

        if(answer === question.correctAnswer) {
            setSelectedAnswer(true)
            console.log('right')
        }
        else {
            setSelectedAnswer(false)
            console.log('wrong')
        }
    };
    
    const [questions, setQuestions] = useState([]);
    const [activeQuestion, setActiveQuestion] = useState(0);
    const [selectedAnswer, setSelectedAnswer] = useState('')
    /*const [result, setResult] = useState({
        score: 0,
        correctAnswers: 0,
        wrongAnswers: 0,
    })*/
    
    useEffect(() => {
        fetch('http://localhost:3001/api/questions')
        .then(response => response.json())
        .then(data => setQuestions(data))
        .catch(error => console.error('Error fetching questions: ', error));

        const { question, choices } = questions[activeQuestion]
    }, []);


    return (
        <Container style={main}>
            {questions.length > 0 && ( // Check if questions array is not empty
                <div>
                    <h3 className="text-center p-3 mb-4 mt-5" style={question_style}>
                        {question}
                    </h3>
                    <ListGroup vertical>
                        {questions[activeQuestion].choices.map((answer) => (
                            <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={() => onClickNext(questions[activeQuestion], answer)}>
                                {answer}
                            </ListGroupItem>
                        ))}
                    </ListGroup>
                </div>
            )}
        </Container>
    );
}

export default Quiz;
