import React from "react";
import { Container, ListGroup, ListGroupItem } from "react-bootstrap";
import { useState } from "react";

const Quiz = () => {

    const alertClicked = () => {
        alert('You clicked one of the buttons!');
    };

    const options = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px", 
    };

    const question = {
        border: 'solid rgba(0.5,0.5,0.5,0.2)',
        borderRadius: "10px",
        backgroundColor: "#2F6BC1",
        color: "#FFFFFF",
    };

    const main = {
        width: "40%",
        margin: "auto",
    }

    const [activeQuestion, setActiveQuestion] = useState(0)
    const [selectedAnswer, setSelectedAnswer] = useState('')
    const [result, setResult] = useState({
        score: 0,
        correctAnswers: 0,
        wrongAnswers: 0,
    })
    
    const { questions } = quiz

    return (
        <Container style={main}>
            <div>
                <h3 className="text-center p-3 mb-4 mt-5" style={question}>
                    {questions[activeQuestion].question}
                </h3>
                <ListGroup vertical>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        1st Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        2nd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        3rd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="mt-3" style={options} action onClick={alertClicked}>
                        4th Answer
                    </ListGroupItem>
                </ListGroup>
            </div>
        </Container>
    );
}

export default Quiz;
