import React from "react";
import { Container, ListGroup, ListGroupItem } from "react-bootstrap";

const Question = () => {

    const alertClicked = () => {
        alert('You clicked one of the buttons!');
    };

    const styles = {
        border: '1px solid rgba(0, 0, 0, 0.05)', 
    };

    return (
        <Container>
            <div>
                <h3>
                    Una Perguntita por favor    
                </h3>
                <ListGroup vertical className="d-grid">
                    <ListGroupItem variant="secondary" className="m-3" action onClick={alertClicked}>
                        1st Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="m-3" style={styles} action onClick={alertClicked}>
                        2nd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="m-3" action onClick={alertClicked}>
                        3rd Answer
                    </ListGroupItem>
                    <ListGroupItem variant="secondary" className="m-3" action onClick={alertClicked}>
                        4th Answer
                    </ListGroupItem>
                </ListGroup>
            </div>
        </Container>
    );
}

export default Question;
